BENCHMARK_DEFINE_F(Byte, MakeLongOctStrWithUint8PointerAndSize)
(benchmark::State &state)
{
    try
    {

        auto test_string = ""_hex;
        auto data = test_string.data();
        auto length = test_string.size();
        for (auto _ : state)
        {
            (void)_;
            (void)_;
            benchmark::DoNotOptimize(OctStr{data, length});
        }
        state.counters["OPS"] = benchmark::Counter(state.iterations(), benchmark::Counter::kIsRate);
        state.counters["Latency"] = benchmark::Counter(state.iterations(), benchmark::Counter::kIsRate | benchmark::Counter::kInvert);
    }
    catch (const std::exception &e)
    {
        state.SkipWithError(e.what());
    }
}
BENCHMARK_REGISTER_F(Byte, MakeLongOctStrWithUint8PointerAndSize)
    ->UseRealTime();

